import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPalette, faUser, faShoppingCart, faBars } from "@fortawesome/free-solid-svg-icons";
import { faFacebookF, faInstagram, faPinterest, faEtsy } from "@fortawesome/free-brands-svg-icons";

const Navbar = ({ currentPage, setCurrentPage, user, showProfile, setShowProfile, cart, setShowCart, showCart, wishlist, setShowCustomRequest }) => {
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  return (
    <nav className="bg-gradient-to-r from-teal-700 to-blue-700 text-white shadow-lg">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        <div className="flex items-center space-x-2 cursor-pointer" onClick={() => setCurrentPage('home')}>
          <FontAwesomeIcon icon={faPalette} className="text-2xl text-pink-400" />
          <span className="text-xl font-bold">Resin Art Creations</span>
        </div>
        <div className="hidden md:flex space-x-6">
          <button onClick={() => setCurrentPage('home')} className="hover:text-pink-300 transition">Home</button>
          <button onClick={() => setCurrentPage('products')} className="hover:text-pink-300 transition">Products</button>
          <button onClick={() => setCurrentPage('about')} className="hover:text-pink-300 transition">About</button>
          <button onClick={() => setCurrentPage('contact')} className="hover:text-pink-300 transition">Contact</button>
        </div>
        <div className="flex items-center space-x-4">
          <button 
            onClick={() => setShowCustomRequest(true)}
            className="hidden md:block bg-pink-500 hover:bg-pink-600 px-3 py-1 rounded-full text-sm transition"
          >
            Request Custom
          </button>
          <div className="relative">
            <button 
              onClick={() => setShowProfile(!showProfile)}
              className="relative p-2 hover:bg-indigo-800 rounded-full transition"
            >
              <FontAwesomeIcon icon={faUser} />
            </button>
            {/* Profile dropdown handled in parent */}
          </div>
          <div className="relative">
            <button 
              onClick={() => setShowCart(!showCart)}
              className="relative p-2 hover:bg-indigo-800 rounded-full transition"
            >
              <FontAwesomeIcon icon={faShoppingCart} />
              {totalItems > 0 && (
                <span className="absolute -top-1 -right-1 bg-teal-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {totalItems}
                </span>
              )}
            </button>
          </div>
          <button className="md:hidden p-2">
            <FontAwesomeIcon icon={faBars} />
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
