import React, { useState } from "react";
import Navbar from "./components/Navbar";
import ProductList from "./components/ProductList";
import Cart from "./components/Cart";
import CustomRequestModal from "./components/CustomRequestModal";
import Notification from "./components/Notification";
import Footer from "./components/Footer";
import AboutPage from "./components/AboutPage";
import ContactPage from "./components/ContactPage";

const initialProducts = [
  {
    id: 1,
    name: 'Ocean Wave Resin Tray',
    price: 45.99,
    description: 'Beautiful ocean-inspired resin tray with wave patterns and shimmering blue tones.',
    image: 'https://images.unsplash.com/photo-1620231109648-2e0f0a4a9e7a?ixlib=rb-4.0.3&auto=format&fit=crop&w=774&q=80',
    category: 'Home Decor',
    inStock: true,
    dateAdded: '2024-07-10',
    sales: 20,
    recommended: true
  },
  {
    id: 2,
    name: 'Galaxy Resin Coaster Set',
    price: 32.50,
    description: 'Set of 4 cosmic-themed coasters with glitter and metallic accents resembling a starry night sky.',
    image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1740&q=80',
    category: 'Functional Art',
    inStock: true,
    dateAdded: '2024-07-12',
    sales: 35,
    recommended: false
  },
  {
    id: 3,
    name: 'Floral Resin Jewelry Box',
    price: 58.75,
    description: 'Elegant jewelry box with preserved flowers encased in clear resin, perfect for storing precious items.',
    image: 'https://images.unsplash.com/photo-1605000797499-95a51c5269ae?ixlib=rb-4.0.3&auto=format&fit=crop&w=1742&q=80',
    category: 'Jewelry',
    inStock: false,
    dateAdded: '2024-07-08',
    sales: 10,
    recommended: true
  },
  {
    id: 4,
    name: 'Geode Resin Wall Art',
    price: 89.99,
    description: 'Stunning wall art piece featuring a realistic geode design with crystal-like resin formations.',
    image: 'https://images.unsplash.com/photo-1620231109648-2e0f0a4a9e7a?ixlib=rb-4.0.3&auto=format&fit=crop&w=774&q=80',
    category: 'Wall Decor',
    inStock: true,
    dateAdded: '2024-07-13',
    sales: 50,
    recommended: false
  },
  {
    id: 5,
    name: 'Resin River Table',
    price: 499.99,
    description: 'Handcrafted live-edge table with a flowing blue resin river running through the center.',
    image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1740&q=80',
    category: 'Furniture',
    inStock: true,
    dateAdded: '2024-07-09',
    sales: 5,
    recommended: false
  },
  {
    id: 6,
    name: 'Personalized Resin Keychain',
    price: 12.99,
    description: 'Customizable keychain with embedded items or text of your choice in durable resin.',
    image: 'https://images.unsplash.com/photo-1605000797499-95a51c5269ae?ixlib=rb-4.0.3&auto=format&fit=crop&w=1742&q=80',
    category: 'Accessories',
    inStock: true,
    dateAdded: '2024-07-11',
    sales: 40,
    recommended: true
  }
];

const HomePage = ({ products }) => (
  <ProductList currentPage="home" products={products} />
);
const ProductsPage = ({ products }) => (
  <ProductList currentPage="products" products={products} />
);

const App = () => {
  const [currentPage, setCurrentPage] = useState("home");
  const [user, setUser] = useState({ name: "John Doe", email: "john@example.com" });
  const [showProfile, setShowProfile] = useState(false);
  const [wishlist, setWishlist] = useState([]);
  const [orders, setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [showCustomRequest, setShowCustomRequest] = useState(false);
  const [notification, setNotification] = useState(null);
  const [products] = useState(initialProducts);

  // Notification helper
  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  let pageContent;
  if (currentPage === "products") {
    pageContent = <ProductsPage products={products} />;
  } else if (currentPage === "about") {
    pageContent = <AboutPage />;
  } else if (currentPage === "contact") {
    pageContent = <ContactPage />;
  } else {
    pageContent = <HomePage products={products} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Navbar
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
        user={user}
        showProfile={showProfile}
        setShowProfile={setShowProfile}
        cart={cart}
        setShowCart={setShowCart}
        showCart={showCart}
        wishlist={wishlist}
        setShowCustomRequest={setShowCustomRequest}
      />
      <main className="flex-1">
        {pageContent}
      </main>
      <Footer />
      {showCart && (
        <Cart
          cart={cart}
          setCart={setCart}
          setShowCart={setShowCart}
          showNotification={showNotification}
        />
      )}
      {showCustomRequest && (
        <CustomRequestModal
          setShowCustomRequest={setShowCustomRequest}
          showNotification={showNotification}
        />
      )}
      {notification && <Notification notification={notification} />}
    </div>
  );
};

export default App;
